import { connect, connection, Connection } from 'mongoose';
import { Todo } from './todo/todo.model';
import { User } from './users/users.model';
import { UserModel } from './users/users.types';
import { TodoModel } from './todo/todo.types';
import { __MONGO_URI__ } from '../utils/config';

interface IModels {
    User: UserModel;
    Todo: TodoModel;
}

export class DB {
    private static instance: DB;

    private _db: Connection;
    private _models: IModels;

    private constructor() {
        connect(__MONGO_URI__, {
            useNewUrlParser: true,
            useUnifiedTopology: true
        });
        this._db = connection;
        this._db.on('open', this.connected);
        this._db.on('error', this.error);

        this._models = {
            User: new User().model,
            Todo: new Todo().model
        };
    }

    public static get Models(): IModels {
        if (!DB.instance) {
            DB.instance = new DB();
        }
        return DB.instance._models;
    }

    private connected() {
        console.log('Mongoose has connected');
    }

    private error(error: Error) {
        console.log('Mongoose has errored', error);
    }
}

import mongoose from 'mongoose';
import TodoSchema from './todo.schema';
import { TodoModel, TodoDocument } from './todo.types';

export class Todo {
    private _model: TodoModel;

    constructor() {
        this._model = mongoose.model<TodoDocument, TodoModel>(
            'todo',
            TodoSchema,
            'todo-list'
        );
    }

    public get model(): TodoModel {
        return this._model;
    }
}

import { Schema } from 'mongoose';
import { sameTitle, setLastUpdated } from './todo.methods';
import { findByCreatedUser, findOneOrCreate } from './todo.statics';
import { ITodo } from './todo.types';

const TodoSchema: Schema<ITodo> = new Schema({
    title: String,
    description: String,
    dateOfEntry: {
        type: Date,
        default: new Date()
    },
    lastUpdated: {
        type: Date,
        default: new Date()
    }
});

TodoSchema.statics.findOneOrCreate = findOneOrCreate;
TodoSchema.statics.findByCreatedUser = findByCreatedUser;

TodoSchema.methods.setLastUpdated = setLastUpdated;
TodoSchema.methods.sameTitle = sameTitle;

export default TodoSchema;

import { Document } from 'mongoose';
import { TodoDocument } from './todo.types';

export async function setLastUpdated(this: TodoDocument): Promise<void> {
    const now = new Date();
    if (!this.lastUpdated || this.lastUpdated < now) {
        this.lastUpdated = now;
        await this.save();
    }
}
export async function sameTitle(this: TodoDocument): Promise<Document[]> {
    return this.model('todo').find({ title: this.title });
}

import { ObjectId } from 'mongoose';
import { TodoDocument, TodoModel, ITodo } from './todo.types';

export async function findOneOrCreate(
    this: TodoModel,
    Todo: ITodo
): Promise<TodoDocument> {
    const record = await this.findOne(Todo);
    if (record) {
        return record;
    } else {
        return this.create(Todo);
    }
}

export async function findByCreatedUser(
    this: TodoModel,
    user_id: ObjectId
): Promise<TodoDocument[]> {
    return this.find({ _id: user_id });
}

import * as Mongoose from 'mongoose';
import UserSchema from './users.schema';
import { UserDocument, UserModel } from './users.types';

export class User {
    private _model: UserModel;
    constructor() {
        this._model = Mongoose.model<UserDocument, UserModel>(
            'user',
            UserSchema
        );
    }

    public get model(): UserModel {
        return this._model;
    }
}

import { Schema } from 'mongoose';
import { sameLastName, setLastUpdated } from './users.methods';
import { User } from './users.model';
import { findByAge, findOneOrCreate } from './users.statics';

const UserSchema: Schema<User> = new Schema({
    firstName: String,
    lastName: String,
    age: Number,
    dateOfEntry: {
        type: Date,
        default: new Date()
    },
    lastUpdated: {
        type: Date,
        default: new Date()
    }
});

UserSchema.statics.findOneOrCreate = findOneOrCreate;
UserSchema.statics.findByAge = findByAge;

UserSchema.methods.setLastUpdated = setLastUpdated;
UserSchema.methods.sameLastName = sameLastName;

export default UserSchema;

import { Document } from 'mongoose';
import { UserDocument } from './users.types';

export async function setLastUpdated(this: UserDocument): Promise<void> {
    const now = new Date();
    if (!this.lastUpdated || this.lastUpdated < now) {
        this.lastUpdated = now;
        await this.save();
    }
}
export async function sameLastName(this: UserDocument): Promise<Document[]> {
    return this.model('user').find({ lastName: this.lastName });
}

import { UserDocument, UserModel, IUser } from './users.types';

export async function findOneOrCreate(
    this: UserModel,
    user: IUser
): Promise<UserDocument> {
    const record = await this.findOne(user);
    if (record) {
        return record;
    } else {
        return this.create(user);
    }
}

export async function findByAge(
    this: UserModel,
    min?: number,
    max?: number
): Promise<UserDocument[]> {
    return this.find({ age: { $gte: min || 0, $lte: max || Infinity } });
}

